import nodemailer from 'nodemailer';

interface EmailOptions {
  to: string;
  subject: string;
  html: string;
}

class EmailService {
  private transporter: nodemailer.Transporter;

  constructor() {
    this.transporter = nodemailer.createTransport({
      service: 'gmail',
      auth: {
        user: process.env.EMAIL_USER || process.env.GMAIL_USER,
        pass: process.env.EMAIL_PASS || process.env.GMAIL_PASS,
      },
    });
  }

  async sendEmail(options: EmailOptions): Promise<boolean> {
    try {
      await this.transporter.sendMail({
        from: process.env.EMAIL_USER || process.env.GMAIL_USER,
        to: options.to,
        subject: options.subject,
        html: options.html,
      });
      return true;
    } catch (error) {
      console.error('Failed to send email:', error);
      return false;
    }
  }

  async sendContentNotification(contentTitle: string, videoUrl: string, status: string): Promise<boolean> {
    const html = `
      <div style="font-family: Arial, sans-serif; max-width: 600px; margin: 0 auto;">
        <h2 style="color: #FF0000;">üé¨ New YouTube Short Generated!</h2>
        <div style="background: #f5f5f5; padding: 20px; border-radius: 8px;">
          <p><strong>Title:</strong> ${contentTitle}</p>
          <p><strong>Status:</strong> ${status}</p>
          ${videoUrl ? `<p><strong>Video URL:</strong> <a href="${videoUrl}">${videoUrl}</a></p>` : ''}
          <p><strong>Generated at:</strong> ${new Date().toLocaleString()}</p>
        </div>
        <p style="margin-top: 20px; color: #666;">
          This is an automated notification from your YouTube Shorts AI Studio.
        </p>
      </div>
    `;

    return await this.sendEmail({
      to: 'mayoaiagent@gmail.com',
      subject: 'üé¨ New YouTube Short Created Successfully!',
      html,
    });
  }

  async sendErrorNotification(error: string, context: string): Promise<boolean> {
    const html = `
      <div style="font-family: Arial, sans-serif; max-width: 600px; margin: 0 auto;">
        <h2 style="color: #CC0000;">‚ö†Ô∏è YouTube Shorts Automation Error</h2>
        <div style="background: #fff5f5; padding: 20px; border-radius: 8px; border-left: 4px solid #CC0000;">
          <p><strong>Error:</strong> ${error}</p>
