import { GoogleGenAI } from "@google/genai";

const ai = new GoogleGenAI({ 
  apiKey: process.env.GEMINI_API_KEY || process.env.GOOGLE_AI_API_KEY || "" 
});

export async function generateScript(): Promise<string> {
  const prompt = `Write a 60-second script for a YouTube Short about an unsolved spiritual mystery or mythology. Use a storytelling hook and end with a cliffhanger. Keep it under 150 words and make it engaging for a young audience.`;

  const response = await ai.models.generateContent({
    model: "gemini-2.5-flash",
    contents: prompt,
  });

  return response.text || "Failed to generate script";
}

export async function generateThumbnailPrompt(script: string): Promise<string> {
  const prompt = `Create a detailed description for a YouTube thumbnail based on this script: "${script}". Make it eye-catching with mysterious elements, glowing effects, and dramatic visuals. The thumbnail should be in 16:9 aspect ratio and attract viewers interested in spiritual mysteries and mythology.`;

  const response = await ai.models.generateContent({
    model: "gemini-2.5-flash",
    contents: prompt,
  });

  return response.text || "Eye-catching thumbnail with glowing runes and hidden mysteries, 16:9";
}

export async function generateThumbnailImage(prompt: string): Promise<Buffer | null> {
  try {
    const response = await ai.models.generateContent({
      model: "gemini-2.0-flash-preview-image-generation",
      contents: [{ role: "user", parts: [{ text: prompt }] }],
      config: {
        responseModalities: ["TEXT", "IMAGE"],
      },
    });

    const candidates = response.candidates;
    if (!candidates || candidates.length === 0) {
      return null;
    }

    const content = candidates[0].content;
    if (!content || !content.parts) {
      return null;
    }

    for (const part of content.parts) {
      if (part.inlineData && part.inlineData.data) {
        return Buffer.from(part.inlineData.data, "base64");
      }
    }

    return null;
  } catch (error) {
    console.error("Failed to generate thumbnail image:", error);
    return null;
  }
}
